import React, { useState, useEffect } from 'react';
import { getWasteCategories } from '../api';

const Dashboard = ({ token }) => {
    const [categories, setCategories] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            const data = await getWasteCategories();
            setCategories(data);
        };
        fetchData();
    }, []);

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Dashboard de Residuos</h2>
            <ul style={styles.list}>
                {categories.map((cat) => (
                    <li key={cat.id} style={styles.item}>
                        {cat.name} - {cat.description}
                    </li>
                ))}
            </ul>
        </div>
    );
};

const styles = {
    container: {
        padding: '20px',
        backgroundColor: '#c8e6c9',
    },
    title: {
        fontSize: '24px',
        color: '#2e7d32',
    },
    list: {
        listStyle: 'none',
    },
    item: {
        margin: '5px 0',
        padding: '10px',
        backgroundColor: '#a5d6a7',
        borderRadius: '5px',
    },
};

export default Dashboard;
